import React from 'react'
import style from './index.module.scss'
export default function Index() {
  const [type, SetType] = React.useState('游乐场')
  const handleSub = (e) => {

    SetType(e.target.parentNode.text || e.target.text)
    console.log(type);
  }
  return (
    <div className={style.subBar} onClick={handleSub}>
      {/* <a className={style.subItem}><div className={`${type === '游乐场' ? style.activeOne : ''} ${style.ball} ${style.itemOne}`}></div>游乐场</a>
      <a className={style.subItem}><div className={`${type === '噩梦' ? style.activeTwo : ''} ${style.ball} ${style.itemTwo}`}></div>噩梦</a>
      <a className={style.subItem}><div className={`${type === 'PH' ? style.activeThree : ''} ${style.ball} ${style.itemThree}`}></div>PH</a> */}
      {/* <div className={style.hole}> <span className={style.ring}></span> </div> */}
      <a className={`${style.subItem} ${type === '游乐场' ? style.active : ''}`}><span /><span /><div className={style.ring} /><div>游乐场</div> </a>
      <a className={`${style.subItem} ${type === '噩梦' ? style.active : ''}`}><span /><span /><div className={style.ring} /><div>噩梦</div> </a>
      <a className={`${style.subItem} ${type === 'PH' ? style.active : ''}`}><span /><span /><div className={style.ring} /><div>PH</div> </a>

    </div>
  )
}
