import React from 'react'
import style from './index.module.scss';
import { Swiper, SwiperSlide } from "swiper/react";

import "swiper/swiper.min.css";
import "swiper/components/pagination/pagination.min.css"
import "swiper/components/navigation/navigation.min.css"
import "./style.css";
import uac1 from 'assets/uac/swiper/uac1.png'
import uac2 from 'assets/uac/swiper/uac2.png'
import uac3 from 'assets/uac/swiper/uac3.png'
import uac4 from 'assets/uac/swiper/uac4.png'
import uac5 from 'assets/uac/swiper/uac5.png'
import uac6 from 'assets/uac/swiper/uac6.png'
import uac7 from 'assets/uac/swiper/uac7.mp4'
import SwiperCore, {
  Pagination, Navigation
} from 'swiper/core';
SwiperCore.use([Pagination, Navigation]);

export default function index() {
  return (
    <div className={style.swiperbox}>
      <div className={style.swiper}>
        <Swiper slidesPerView={1} spaceBetween={30} loop={true} pagination={{
          "clickable": true
        }} navigation={true} >
          <SwiperSlide ><img src={uac1} alt="" /></SwiperSlide>
          <SwiperSlide ><img src={uac2} alt="" /></SwiperSlide>
          <SwiperSlide ><img src={uac3} alt="" /></SwiperSlide>
          <SwiperSlide ><img src={uac4} alt="" /></SwiperSlide>
          <SwiperSlide ><img src={uac5} alt="" /></SwiperSlide>
          <SwiperSlide ><img src={uac6} alt="" /></SwiperSlide>
          <SwiperSlide ><video src={uac7} autoplay loop muted /></SwiperSlide>
        </Swiper>
      </div>
      <div className={style.swiperText}>
        <span>免费, 刺激的RPG团队合作游戏</span>
        <ul>
          <li><div className={style.triangle}></div> 不同职业，不同体验</li>
          <li><div className={style.triangle}></div> 团队合作，配合默契</li>
          <li><div className={style.triangle}></div> 丰富剧情，享受乐趣</li>
        </ul>
      </div>
    </div>
  )
}
